import ReactDOM from "react-dom";
//import Main from "./Main";
import React, { Component } from "react";
import "./index.css";



class Corps extends React.Component {

	ChangeColorFunc()
	{
		document.getElementById("myDIV").style.backgroundColor = "lightblue";
	}



  render() {
    return (
     <div>
    <div class="container-fluid">
      <div class="row">
        <div class="col-lg-3 col-md-3 col-sm-3 col-xs-3">
        </div>

        <div id="myDIV" style={{backgroundColor: "orange"},{border: "solid"}} class="col-lg-6 col-md-6 col-sm-6 col-xs-6">

          <p>Nom :</p>
          <p>Prenom :</p>
          <p>Date de Naissance :</p>
          <button class="w3-button w3-xlarge w3-circle w3-black" onClick={() => this.ChangeColorFunc()}>Change Style</button>
        </div>

        <div class="col-lg-3 col-md-3 col-sm-3 col-xs-3">
        </div>
      </div>
    </div>
    </div>
    );
  }
}

//export default NavigationCorps;

const rootElement3= document.getElementById("root3");
ReactDOM.render(<Corps />, rootElement3);
