import React, { Component } from "react";
import "./index.css";



class Corps extends React.Component {

	constructor(props) {
    super(props);

    this.state = {
      name: "Claude"
  };
}


	ChangeColorFunc()
	{
		document.getElementById("myDIV").style.backgroundColor = "lightblue";
	}

