    return (
     <div>
    <div class="container-fluid">
      <div class="row">
        <div class="col-lg-3 col-md-3 col-sm-3 col-xs-3">
        </div>

        <div id="myDIV" style={{backgroundColor: "orange"},{border: "solid"}} class="col-lg-6 col-md-6 col-sm-6 col-xs-6">

          <p>Nom : {this.state.name}</p>
          <p>Prenom :</p>
          <p>Date de Naissance :</p>
          <button class="w3-button w3-xlarge w3-circle w3-black" onClick={() => this.ChangeColorFunc()}>Change Style</button>
        </div>

        <div class="col-lg-3 col-md-3 col-sm-3 col-xs-3">
        </div>
      </div>
    </div>
    <button onClick={()=>this.AffichageInfosJeanne()}>bouttonJeanne</button>
    </div>
    );
  }
}




